import React, { Component } from 'react';
import { withStyles, Button } from "@material-ui/core"
import PropTypes from 'prop-types';
import backgroundInfo from '../information'

const styles = theme => ({
  tableContainer: {
    width: '80%',
    display: 'table',
    margin: 'auto',
    border: '2px solid blue',
    padding: '10px 0 10px 0',
    marginBottom: '3%'
  },
  contentContainer: {
    display:'inline-flex',
    margin: 'auto'
  },
  contentTitle: {
    width: '90%',
    fontSize: 28,
    fontWeight: 600,
    color: '#696969',
    border: '1px solid red'  
  },
  contentDescription: {
    width: '90%',
    display: 'table-flex',
    margin: 'auto',
    textAlign: 'center',
    fontSize: 22,
    fontWeight: 400,
    color: '#696969',
    minHeight: 100,
  },
  buttonContainer: {
    width: '90%',
    display:'table-flex',
    border: '1px solid orange',
    borderTop: '2px solid #696969'
  },
  buttons: {
    width: "fit-content",
    display: 'table-flex',
    margin: 'auto',
    textAlign: 'center',
    minHeight: 100

  }
})

class Background extends Component {
  state = {
    title: 'My background',
    content: 'Check out my a quick summary of my background by clicking through this table'
  }

  updateContent = (content) => {
    console.log("content", content)
  }

  render() {
    const { classes } = this.props;

    return (
      <div className={classes.tableContainer}>
        <h2 className={classes.contentTitle}>{ this.state.title }</h2>
        <p className={classes.contentDescription}>{ this.state.content }</p>
        {
          backgroundInfo.map((tableContent, index) => {
            return (
              <div key={index} className={classes.contentContainer}>
                <div className={classes.buttonContainer}>
                  <div className={classes.buttons} onClick={() => this.updateContent(backgroundInfo)}>{ tableContent.title }</div>
                </div>
              </div>
            )
          })
        }
      </div>
    )
  }
}

Background.propTypes = {
    classes: PropTypes.object.isRequired,
  };

export default withStyles(styles)(Background);
